mapper.select:
		select *
		from attractions


				sido_code = #{sidoCode}


				and gugun_code = #{gugunCode}


				and content_type_id = #{contentTypeId}


				and title like
				concat('%', #{title}, '%')


            		and content_id in (
	                select content_id
	                from pet_attractions
	                where acmpyPsblCpam = #{keyword}
	                   or acmpyPsblCpam = '모든 견종'
	            )



			limit #{page_start}, #{page_size}

select content_id
from pet_attractions
where acmpyPsblCpam = #{keyword}
or acmpyPsblCpam = "모든 견종"

	limit #{page_start}, #{page_size}

select content_id
from hotplace
where user_id = #{user_id};
