mapper.update:
UPDATE travel_plans
SET
title = #{title}, end_at = now(), is_public = #{is_public},
description = #{description}
WHERE id = #{plan_id} and user_id =
#{user_id}

update travel_plans
set view_cnt = view_cnt + 1
where id=#{id}

mapper.select:
select *
from users
where user_id=#{user_id} and email=#{email};

select user_id
from travel_plan_comments
where id = #{id};

select *
from travel_plan_comments
where parent_comment_id = #{parent_comment_id}
order by created_at;

select plan_id
from plans_favorites
where user_id = #{user_id};

select count(*)
from plans_favorites
where user_id=#{user_id} and plan_id=#{plan_id}
